import "./Navbar.css"
import React from 'react'
import { Image,Flex,Text,Input,Box } from '@chakra-ui/react'
import {  NavLink } from "react-router-dom";
const menulogo="data:image/png;base64,";
const searchlogo="data:image/png;base64,";
const orderlogo="data:image/png;base64,";
const cartlogo="https://www.kindpng.com/picc/m/12-123316_paytm-mall-icon-png-image-free-download-searchpng.png";
const loginlogo="data:image/png;base64,";

const category = [
  "Jewellery",
  "Electronics",
  "Fashion",
  "Decoration",
  "Glasses"
];

const Navbar = () => {
  const [open, setOpen] = React.useState(false);
  const menuref = React.useRef();

  React.useEffect(() => {
    let handler = (e) => {
      if (!menuref.current.contains(e.target)) {
        setOpen(false);
      }
    };
    document.addEventListener("mousedown", handler);
  });
  return (
    <Box>
      <Image
        className="Logo"
        src="data:image/png;base64,"
        alt="Paytm-mall"
      />
      <Flex p={"0 1%"} gap="1%" h={"40px"} fontSize={"16px"} mt={"-2vh"}>
        <Flex
          align="center"
          border={"1px solid gray"}
          p="0 0.5%"
          borderRadius={"5px"}
          className="flex-class"
          onClick={() => {
            setOpen(!open);
          }}
          ref={menuref}
        >
          <Image src={menulogo} alt="" w="25px" h="20px" />
          <Text w={"12vw"}>Shop by category</Text>
        </Flex>
        <Flex className="flex-class">
          <Input
            type="search"
            border={"1px solid gray"}
            placeholder="Search for a Product,Brand or Category"
            w={"48vw"}
            h="100%"
          />
          <Image src={searchlogo} h="25px" ml={"-6%"} mt={"1.5%"} />
        </Flex>
        <Flex align={"center"} gap="0.8vw" ml={"-2%"} className="flex-class">
          <Image src={orderlogo} h={"25px"} />
          <Text>My Orders</Text>
        </Flex>
        <Flex align={"center"} gap="0.8vw" className="flex-class">
          <Image src={cartlogo} h={"25px"} />
          <Text w={"7vw"} lineHeight="18px">
            No Items in the Bag
          </Text>
        </Flex>
        <Flex align={"center"} gap="0.8vw" className="flex-class">
          <Image src={loginlogo} h={"25px"} />
          <Text>Log In/Sign Up</Text>
        </Flex>
      </Flex>
      <Flex
        padding={"1% 5%"}
        backgroundColor="white"
        width={"80vw"}
        className={`category-menu ${open ? `active` : `inactive`}`}
      >
        {/* <Box  lineHeight="30px">
          <Flex align="center" border={"1px solid gray"} p="0 1%" borderRadius={"5px"} className="flex-class">
            <Image src={menulogo} alt="" w="25px" h="20px"/>
            <Text w={"12vw"}>Shop by category</Text>            
          </Flex>
          
        </Box> */}
        <Text color={"red"} textAlign="left">
          GIFT CARDS
        </Text>

        <Box paddingLeft={"10%"} textAlign="left" lineHeight={"30px"}>
          {category.map((ele) => (
            <p>{ele}</p>
          ))}
        </Box>
      </Flex>
    </Box>
  );
};

export default Navbar;

const ShopCategory = () => {
  return <div></div>;
};
